mapper.select:
SELECT count(1)
FROM CM_KCD K LEFT JOIN (
        SELECT * FROM MAP_KCD_SCT WHERE MAP_VER = #{mapVer}

            and MAP_STAT_CD = #{mapStatCd}

) M ON K.KCD_CD = M.ORI_CD
LEFT OUTER JOIN CM_SNOMEDCT S
ON M.SCT_ID=S.SCT_ID
LEFT OUTER JOIN EMR.MCODE CD
ON left(M.MAP_STAT_CD,1)=CD.CD_ID
AND CD.CD_GRP='MAP_STAT_CD'
AND CD.CD_STAT_CD='0'


        K.KCD_CD=#{kcdCd}

SELECT count(1)
FROM CM_KCD K 


        K.KCD_CD=#{kcdCd}

SELECT K.KCD_CD kcdCd, K.KCD_ENG kcdEng, K.KCD_KOR kcdKor, M.UDT_DT udtDt, M.SCT_ID sctId, M.MAP_STAT_CD mapStatCd, S.SCT_TERM sctTerm, CD.CD_DSCRT mapStatNm FROM CM_KCD K LEFT JOIN ( SELECT * FROM MAP_KCD_SCT WHERE MAP_VER = #{mapVer}
and MAP_STAT_CD = #{mapStatCd}
) M ON K.KCD_CD = M.ORI_CD LEFT OUTER JOIN CM_SNOMEDCT S ON M.SCT_ID=S.SCT_ID LEFT OUTER JOIN EMR.MCODE CD ON left(M.MAP_STAT_CD,1)=CD.CD_ID AND CD.CD_GRP='MAP_STAT_CD' AND CD.CD_STAT_CD='0'
K.KCD_CD=#{kcdCd}
limit ${limit} offset ${offset};

SELECT count(1)
FROM CM_KCD K INNER JOIN (
        SELECT * FROM MAP_KCD_SCT WHERE MAP_VER = ${mapVer} 

            and MAP_STAT_CD = #{mapStatCd}

) M ON K.KCD_CD = M.ORI_CD
LEFT OUTER JOIN CM_SNOMEDCT S
ON M.SCT_ID=S.SCT_ID
LEFT OUTER JOIN EMR.MCODE CD
ON left(M.MAP_STAT_CD,1)=CD.CD_ID
AND CD.CD_GRP='MAP_STAT_CD'
AND CD.CD_STAT_CD='0'


        K.KCD_CD=#{kcdCd}

SELECT count(*)
FROM CM_KCD K 
WHERE KCD_CD IN (
        SELECT ORI_CD FROM MAP_KCD_SCT WHERE MAP_VER = #{mapVer} 

            and MAP_STAT_CD = #{mapStatCd}

)


        K.KCD_CD=#{kcdCd}



   limit ${limit} offset ${offset};

SELECT K.KCD_CD kcdCd, K.KCD_ENG kcdEng, K.KCD_KOR kcdKor, K.UDT_DT udtDt, M.SCT_ID sctId, S.SCT_TERM sctTerm, M.MAP_STAT_CD mapStatCd, CD.CD_DSCRT mapStatNm FROM CM_KCD K INNER JOIN ( SELECT * FROM MAP_KCD_SCT WHERE MAP_VER = ${mapVer}
and MAP_STAT_CD = #{mapStatCd}
) M ON K.KCD_CD = M.ORI_CD LEFT OUTER JOIN CM_SNOMEDCT S ON M.SCT_ID=S.SCT_ID LEFT OUTER JOIN EMR.MCODE CD ON left(M.MAP_STAT_CD,1)=CD.CD_ID AND CD.CD_GRP='MAP_STAT_CD' AND CD.CD_STAT_CD='0'
K.KCD_CD=#{kcdCd}
limit ${limit} offset ${offset};

select count(1)
from CM_KCD where KCD_CD not in (
    select distinct ORI_CD from MAP_KCD_SCT where MAP_VER = ${mapVer} 

            and MAP_STAT_CD = #{mapStatCd}

)


        K.KCD_CD=#{kcdCd}

SELECT count(1)
FROM CM_KCD K 
WHERE KCD_CD NOT IN (
        SELECT ORI_CD FROM MAP_KCD_SCT WHERE MAP_VER = #{mapVer} 

            and MAP_STAT_CD = #{mapStatCd}


)


        K.KCD_CD=#{kcdCd}

select KCD_CD kcdCd,
   KCD_ENG kcdEng,
   KCD_KOR kcdKor

from CM_KCD where KCD_CD not in (
    select distinct ORI_CD from MAP_KCD_SCT where MAP_VER = ${mapVer} 

            and MAP_STAT_CD = #{mapStatCd}

)


        and KCD_CD=#{kcdCd}



   limit ${limit} offset ${offset};

SELECT K.KCD_CD kcdCd, K.KCD_ENG kcdEng, K.KCD_KOR kcdKor, M.SCT_ID sctId, S.SCT_TERM sctTerm, M.MAP_STAT_CD mapStatCd, CD.CD_DSCRT mapStatNm, M.UDT_DT udtDt from EMR.CM_KCD K left outer join EMR.MAP_KCD_SCT M on K.KCD_CD=M.ORI_CD AND M.ORI_TP_CD='KCD' left outer join EMR.CM_SNOMEDCT S on M.SCT_ID=S.SCT_ID left outer join EMR.MCODE CD ON left(M.MAP_STAT_CD,1)=CD.CD_ID AND CD.CD_GRP='MAP_STAT_CD' AND CD.CD_STAT_CD='0' where K.ICD_CD is null
and K.KCD_CD=#{kcdCd}
limit ${limit} offset ${offset};
