mapper.select:
SELECT K.KCD_CD kcdCd, K.KCD_ENG kcdEng, K.KCD_KOR kcdKor, M.UDT_DT udtDt, M.SCT_ID sctId, M.MAP_STAT_CD mapStatCd, S.SCT_TERM sctTerm, CD.CD_DSCRT mapStatNm FROM CM_KCD K LEFT JOIN ( SELECT * FROM MAP_KCD_SCT WHERE MAP_VER = #{mapVer}
and MAP_STAT_CD = #{mapStatCd}
) M ON K.KCD_CD = M.ORI_CD LEFT OUTER JOIN CM_SNOMEDCT S ON M.SCT_ID=S.SCT_ID LEFT OUTER JOIN EMR.MCODE CD ON left(M.MAP_STAT_CD,1)=CD.CD_ID AND CD.CD_GRP='MAP_STAT_CD' AND CD.CD_STAT_CD='0'
and K.KCD_CD=#{kcdCd}
limit ${limit} offset ${offset};

select KCD_CD kcdCd,
   KCD_ENG kcdEng,
   KCD_KOR kcdKor,
   '' udtDt,
   '' sctId,
   ''  sctTerm,
   ''  mapStatCd,
   ''  mapStatNm
from CM_KCD where KCD_CD not in (
    select distinct ORI_CD from MAP_KCD_SCT where MAP_VER = ${mapVer} and MAP_STAT_CD = #{mapStatCd}
)

    limit ${limit} offset ${offset};

SELECT count(*)
FROM CM_KCD K 
WHERE KCD_CD IN (
        SELECT ORI_CD FROM MAP_KCD_SCT WHERE MAP_VER = #{mapVer} and MAP_STAT_CD = #{mapStatCd}
);

SELECT count(*)
FROM CM_KCD K 
WHERE KCD_CD NOT IN (
        SELECT ORI_CD FROM MAP_KCD_SCT WHERE MAP_VER = #{mapVer} and MAP_STAT_CD = #{mapStatCd}
);

SELECT count(*)
FROM CM_KCD K LEFT JOIN (
        SELECT * FROM MAP_KCD_SCT WHERE MAP_VER = #{mapVer} and MAP_STAT_CD = #{mapStatCd}
) M ON K.KCD_CD = M.ORI_CD;
