mapper.select:
SELECT K.KCD_CD kcdCd,
   K.KCD_ENG kcdEng,
   K.KCD_KOR kcdKor,
   K.UDT_DT udtDt,
   M.SCT_ID sctId
FROM CM_KCD K LEFT JOIN (
        SELECT * FROM MAP_KCD_SCT WHERE MAP_VER = ${mapVer}
) M ON K.KCD_CD = M.KCD_CD limit ${limit} offset ${offset};

SELECT K.KCD_CD kcdCd,
   K.KCD_ENG kcdEng,
   K.KCD_KOR kcdKor,
   K.UDT_DT udtDt,
   M.SCT_ID sctId
FROM CM_KCD K INNER JOIN (
        SELECT * FROM MAP_KCD_SCT WHERE MAP_VER = ${mapVer}
) M ON K.KCD_CD = M.KCD_CD limit ${limit} offset ${offset};

select KCD_CD kcdCd,
   KCD_ENG kcdEng,
   KCD_KOR kcdKor,
   UDT_DT udtDt,
   null sctId
from CM_KCD where KCD_CD not in (
    select distinct ORI_CD from MAP_KCD_SCT where MAP_VER = ${mapVer}
) limit ${limit} offset ${offset};
