mapper.select:
SELECT CM.KD_CD kdCd, CM.DRUG_NM_ENG drugNmEng, CM.DRUG_NM_KOR drugNmKor, CM.SUB_ALT_KEY subAltKey, MAP2.UDT_DT udtDt, MAP2.SCT_ID sctId, CS.SCT_TERM sctTerm, MAP2.MAP_STAT_CD mapStatCd, CD.CD_DSCRT cdDscrt FROM CM_MEDICINE CM LEFT OUTER JOIN ( SELECT * FROM MAP_KCD_SCT2 WHERE ORI_TP_CD = 'MEDI' ) MAP2 ON CM.KD_CD = MAP2.ORI_CD LEFT OUTER JOIN CM_SNOMEDCT CS ON MAP2.SCT_ID = CS.SCT_ID LEFT OUTER JOIN MCODE CD ON LEFT(MAP2.MAP_STAT_CD, 1) = CD.CD_ID AND CD.CD_GRP = 'MAP_STAT_CD' AND CD.CD_STAT_CD = '0'
CM.KD_CD = #{kdCd}
limit ${limit} offset ${offset}
;

SELECT CM.KD_CD kdCd, CM.DRUG_NM_ENG drugNmEng, CM.DRUG_NM_KOR drugNmKor, CM.SUB_ALT_KEY subAltKey, MAP2.UDT_DT udtDt, MAP2.SCT_ID sctId, S.SCT_TERM sctTerm, MAP2.MAP_STAT_CD mapStatCd, CD.CD_DSCRT cdDscrt FROM CM_MEDICINE CM INNER JOIN ( SELECT * FROM MAP_KCD_SCT2 WHERE MAP_VER = 0 AND ORI_TP_CD = 'MEDI' ) MAP2 ON CM.KD_CD = MAP2.ORI_CD LEFT OUTER JOIN CM_SNOMEDCT S ON MAP2.SCT_ID = S.SCT_ID LEFT OUTER JOIN MCODE CD ON LEFT(MAP2.MAP_STAT_CD, 1) = CD.CD_ID AND CD.CD_GRP = 'MAP_STAT_CD' AND CD.CD_STAT_CD = '0'
CM.KD_CD = #{kdCd}
limit ${limit} offset ${offset}
;

SELECT CM.KD_CD kdCd, CM.DRUG_NM_ENG drugNmEng, CM.DRUG_NM_KOR drugNmKor, CM.SUB_ALT_KEY subAltKey FROM CM_MEDICINE CM WHERE KD_CD NOT IN ( SELECT DISTINCT ORI_CD FROM MAP_KCD_SCT2 WHERE MAP_VER = 0 AND ORI_TP_CD = 'MEDI' )
and M.KD_CD = #{kdCd}
limit ${limit} offset ${offset}
;

SELECT count(1)
FROM CM_MEDICINE K
WHERE KD_CD NOT IN (
SELECT ORI_CD FROM MAP_KCD_SCT2 WHERE MAP_VER = #{mapVer}

)

SELECT COUNT(1) FROM MAP_KCD_SCT2 MAP2 LEFT OUTER JOIN ( SELECT * FROM CM_MEDICINE ) CM ON MAP2.ORI_CD = CM.KD_CD LEFT OUTER JOIN CM_SNOMEDCT S ON MAP2.SCT_ID = S.SCT_ID LEFT OUTER JOIN MCODE CD ON LEFT(MAP2.MAP_STAT_CD, 1) = CD.CD_ID AND CD.CD_GRP = 'MAP_STAT_CD' AND CD.CD_STAT_CD = '0'
CM.KD_CD = #{kdCd}
;

SELECT count(1) FROM CM_MEDICINE CM INNER JOIN ( SELECT * FROM MAP_KCD_SCT2 WHERE MAP_VER = 0 AND ORI_TP_CD = 'MEDI' ) MAP2 ON CM.KD_CD = MAP2.ORI_CD LEFT OUTER JOIN CM_SNOMEDCT S ON MAP2.SCT_ID = S.SCT_ID LEFT OUTER JOIN MCODE CD ON LEFT(MAP2.MAP_STAT_CD, 1) = CD.CD_ID AND CD.CD_GRP = 'MAP_STAT_CD' AND CD.CD_STAT_CD = '0'
CM.KD_CD = #{kdCd}
;

SELECT count(1)
FROM CM_MEDICINE CM
WHERE KD_CD NOT IN (
    SELECT DISTINCT ORI_CD
        FROM MAP_KCD_SCT2
        WHERE MAP_VER = 0
    AND ORI_TP_CD = 'MEDI'
)

    CM.KD_CD = #{kdCd}

;
